float* frame(float *audio, float *buffer, float *end)
{
    for (int i = 0; i < N_FFT; i++)
    {
        if (audio <= end)
        {
            buffer[i] = *audio;
            audio++;
        }
        else
        {
            buffer[i] = 0.0f;
        }
    }
    
    return audio + HOP_LENGTH - N_FFT;
}

void extractPowerSpectrum(float *audio, float powerSpectrum[SAMPLE_RATE / HOP_LENGTH + 1][N_FFT/2 + 1])
{
    float *audioTrace = audio;
    float *end = audio + SAMPLE_RATE;
    
    int count = 0;
    while (audioTrace < end && count < SAMPLE_RATE / HOP_LENGTH + 1)
    {
        audioTrace = frame(audioTrace, vReal, end);
        FFT.windowing(vReal, N_FFT, FFT_WIN_TYP_HAMMING, FFT_FORWARD);
        FFT.compute(vReal, vImag, N_FFT, FFT_FORWARD);
        FFT.complexToMagnitude(vReal, vImag, N_FFT);

        //place the magnitudes inside a matrix and square them
        for (int i = 0; i < N_FFT/2 + 1; i++)
        {
            powerSpectrum[count][i] = vReal[i] * vReal[i];
        }
        count++;

        //empty vImag
        for (int i = 0; i < N_FFT; i++)
        {
            vImag[i] = 0.0f;
        }
    }
}

void extractMelEnergies(float *audio, float melEnergies[SAMPLE_RATE / HOP_LENGTH + 1][N_MELS])
{
    int numFrames = SAMPLE_RATE / HOP_LENGTH + 1;
    int lenFrames = N_FFT/2 + 1;
    float powerSpectrum[SAMPLE_RATE / HOP_LENGTH + 1][N_FFT/2 + 1];
    extractPowerSpectrum(audio, powerSpectrum);

    for (int frameIdx = 0; frameIdx < numFrames; frameIdx++)
    {
        for (int melIdx = 0; melIdx < N_MELS; melIdx++)
        {
            float energy =  0.0f;
            for (int binIdx = 0; binIdx < lenFrames; binIdx++)
            {
                energy += powerSpectrum[frameIdx][binIdx] * melFilterbank[melIdx][binIdx];
            }
            
            melEnergies[frameIdx][melIdx] = energy;
        }
    }
}

void powerTodB(float melspectrogram[SAMPLE_RATE / HOP_LENGTH + 1][N_MELS]) 
{
    float max = 0;
    for (int i = 0; i < SAMPLE_RATE / HOP_LENGTH + 1; i++)
    {
        for (int j = 0; j < N_MELS; j++)
        {
            if (melspectrogram[i][j] > max)
                max = melspectrogram[i][j];
        }
    }

    for (int i = 0; i < SAMPLE_RATE / HOP_LENGTH + 1; i++)
    {
        for (int j = 0; j < N_MELS; j++)
        {
            melspectrogram[i][j] = 10 * log10((melspectrogram[i][j] + 1e-6) / max);
        }
    }
}

void melspectrogram(float *audio, float melspectrogram[SAMPLE_RATE / HOP_LENGTH + 1][N_MELS])
{
    extractMelEnergies(audio, melspectrogram);
    powerTodB(melspectrogram);
}