void printSpectrogram(float melspectrogramDst[N_MELS][NUM_FRAMES])
{
  Serial.println("mel_energies = np.array([");
  for (int i = 0; i < N_MELS; i++)
  {
    Serial.print("[");
    for (int j = 0; j < NUM_FRAMES; j++)
    {
      Serial.print(melspectrogramDst[i][j]);
      if (j < NUM_FRAMES - 1)
      {
        Serial.print(", ");
      }
    }
    Serial.print("]");
    if (i < N_MELS - 1)
    {
      Serial.println(",");
    }
  }
  Serial.println("])");
}

